var utils = require('twainetUtils');
var module = require('twainetModule');
var conn = require('clientServerConnector');
var net = require('net');

exports.ClientServerModule = function(name) {
	module.Module.call(this, name, conn.ClientServerConnector);
	this.client = "ClientName";
	this.server = "ServerName";
}

utils.extend(exports.ClientServerModule, module.Module);

exports.ClientServerModule.prototype.StartServer = function(port) {
	var self = this;
	var server = net.createServer(function(c) {
		self.addConnector(c, self.client);
	});
	
	server.listen(port, function() { //'listening' listener
		console.log('server bound');
	});
}

